notifyChannel:
function(streamer) { const channelId = this.getChannelId(); if (!channelId) return; const channel = this.client.channels.cache.get(channelId); if (channel) { let url = streamer.type === 'twitch' ? `https://twitch.tv/${streamer.name}` : `https://kick.com/${streamer.name}`;

        // Para Twitch, tente buscar a thumbnail
        let embed = null;
        if (streamer.type === 'twitch') {
            // Thumbnail padrão da Twitch (substitua {width} e {height} se quiser)
            const thumb = `https://static-cdn.jtvnw.net/previews-ttv/live_user_${streamer.name.toLowerCase()}.jpg?width=640&height=360`;
            embed = {
                title: `Twitch: ${streamer.name}`,
                url: url,
                description: `O ${streamer.name} está ao vivo! @everyone`,
                image: { url: thumb }
            };
        } else {
            embed = {
                title: `Kick: ${streamer.name}`,
                url: url,
                description: `O ${streamer.name} está ao vivo! @everyone`
            };
        }

        const row = new ActionRowBuilder().addComponents(
            new ButtonBuilder()
                .setLabel('Acessar')
                .setStyle(ButtonStyle.Link)
                .setURL(url)
        );
        channel.send({
            embeds: [embed],
            components: [row]
        });
    }
  }
